mapper.delete:
DELETE FROM

WHERE

	 AND clientName = #{clientName} 
	 AND company = #{company} 
	 AND link = #{link} 
	 AND status = #{status}

mapper.update:
UPDATE


	 , clientName LIKE CONCAT( '%', '${clientName}', '%' ) 
	 , company LIKE CONCAT( '%', '${company}', '%' ) 
	 , link LIKE CONCAT( '%', '${link}', '%' ) 
	 , status = #{status} 

WHERE clientID = #{primaryKey}

mapper.select:
SELECT COUNT(*) FROM


	 AND clientName = #{clientName} 
	 AND company = #{company} 
	 AND link = #{link} 
	 AND status = #{status}
